        <script type="text/javascript">
            
            function updateCartItem(obj,rowid,price){
                var id=$(obj).attr('data');
                var qty=$('#'+id+'qty').val();
                $.ajax({
                    dataType:'json',
                    url: "https://www.thevintagegarments.com/updateItemQty/" + qty+"/"+rowid+"/"+price,
                    success: function (result) {
                        // alert(result.subtotal);
                        $('#'+id+'sub').html(result.subtotal);
                        $('#ftotal').html(result.ftotal);
                    }
                });
            };
        </script>
