            form .stars input[type="radio"].star-4:checked ~ span {
                width: 80%;
            }
            
            form .stars input[type="radio"].star-3:checked ~ span {
                width: 60%;
            }
            
            form .stars input[type="radio"].star-2:checked ~ span {
                width: 40%;
            }
            
            form .stars input[type="radio"].star-1:checked ~ span {
                width: 20%;
            }
            
            form .stars label {
                display: block;
                width: 30px;
