                                                                                                                                                                                                                                                                                                                            <script>
                                                                                                                                                                                                                                                                                                                                $('.js-color-toggle').click(function(){
                                                                                                                                                                                                                                                                                                                                    pid=$(this).attr('pid');
                                                                                                                                                                                                                                                                                                                                    cid=$(this).attr('cid');
                                                                                                                                                                                                                                                                                                                                    size=$(this).parents('div[class="prd-inside"]').find( ".prd-size" );
                                                                                                                                                                                                                                                                                                                                    $.ajax({
                                                                                                                                                                                                                                                                                                                                        url:'https://www.thevintagegarments.com/gethomesizes/'+pid+'/'+cid,
                                                                                                                                                                                                                                                                                                                                        type: 'POST',
                                                                                                                                                                                                                                                                                                                                        success: function ( result ) {
                                                                                                                                                                                                                                                                                                                                            size.html(result);
                                                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                                                    });
                                                                                                                                                                                                                                                                                                                                });
                                                                                                                                                                                                                                                                                                                                $('body').on('click', '.addtocart', function() {
                                                                                                                                                                                                                                                                                                                                    sid=$(this).parents('div[class="prd-inside"]').find( ".optionsSelect02" ).children("option:selected").val();
                                                                                                                                                                                                                                                                                                                                    img=$(this).parents('div[class="prd-inside"]').find( 'li[class="active"]>a' );
                                                                                                                                                                                                                                                                                                                                    pid=img.attr('pid');
                                                                                                                                                                                                                                                                                                                                    cid=img.attr('cid');
                                                                                                                                                                                                                                                                                                                                    qty=$(this).parents('div[class="prd-inside"]').find( ".qty-input" ).val();
                                                                                                                                                                                                                                                                                                                                    if(jQuery.type(qty) === "undefined"){
                                                                                                                                                                                                                                                                                                                                        qty=1;
                                                                                                                                                                                                                                                                                                                                    }
                                                                                                                                                                                                                                                                                                                                    $.ajax({
                                                                                                                                                                                                                                                                                                                                        url:'https://www.thevintagegarments.com/addtocart/',
                                                                                                                                                                                                                                                                                                                                        data:{'pid':pid,'cid':cid,'sid':sid,'qty':qty},
                                                                                                                                                                                                                                                                                                                                        type: 'POST',
                                                                                                                                                                                                                                                                                                                                        success: function ( result ) {
                                                                                                                                                                                                                                                                                                                                            location.reload();
                                                                                                                                                                                                                                                                                                                                        }
                                                                                                                                                                                                                                                                                                                                    });
                                                                                                                                                                                                                                                                                                                                });
                                                                                                                                                                                                                                                                                                                            </script>
